package com.onclass.bootcamp.adapters.driven.jpa.mysql.mapper;import com.onclass.bootcamp.adapters.driven.jpa.mysql.entity.TechnologyEntity;import com.onclass.bootcamp.domain.model.Technology;import org.mapstruct.Mapper;import org.mapstruct.Mapping;import java.util.List;@Mapper(componentModel = "spring")public interface ITechnologyEntityMapper {    @Mapping(target = "name", source = "name")    @Mapping(target = "capacities", ignore = true)    TechnologyEntity toEntity(Technology technology);    List<Technology> toModelList(List<TechnologyEntity> technologyEntities);}